public class amazingSubArray {
    public static void main(String[] args) {
        System.out.println(solve(""));
    }

    public static int solve(String A){
        A = A.toLowerCase();
        int ans = 0;
        int i=0;
        while(i<A.length()){
            if(A.charAt(i) == 'a' || A.charAt(i) == 'e' || A.charAt(i) == 'i' || A.charAt(i) == 'o' || A.charAt(i) == 'u'){
                ans+=A.length()-i;
            }
            i++;
        }
        return ans%10003;
    }
}
